BEGIN;
TRUNCATE order_details, orders, books, authors, genres, publishers, users, roles, pickup_points RESTART IDENTITY CASCADE;
SELECT setval(pg_get_serial_sequence('authors','author_id'), COALESCE((SELECT MAX(author_id) FROM authors),0), true);
SELECT setval(pg_get_serial_sequence('genres','genre_id'), COALESCE((SELECT MAX(genre_id) FROM genres),0), true);
SELECT setval(pg_get_serial_sequence('publishers','publisher_id'), COALESCE((SELECT MAX(publisher_id) FROM publishers),0), true);
SELECT setval(pg_get_serial_sequence('books','book_id'), COALESCE((SELECT MAX(book_id) FROM books),0), true);
SELECT setval(pg_get_serial_sequence('pickup_points','pickup_point_id'), COALESCE((SELECT MAX(pickup_point_id) FROM pickup_points),0), true);
SELECT setval(pg_get_serial_sequence('users','user_id'), COALESCE((SELECT MAX(user_id) FROM users),0), true);
COMMIT;